public class Math {
    public static void main(String [] args) {

    System.out.println(java.lang.Math.abs(-3.143)); //abs() method - returns the absolute (positive) value of a number
    System.out.println(java.lang.Math.acos(1)); //acos() method - returns the arc cosine value of a number
    System.out.println(java.lang.Math.asin(0.5)); //acos() method - returns the arc cosine value of a number
    System.out.println(java.lang.Math.atan(-3.14)); //atan() method - returns the arctangent of x as a numeric value between -PI/2 and PI/2 radians
    System.out.println(java.lang.Math.atan2(1,1)); //atan2() method - returns the angle theta from the conversion of rectangular coordinates (x, y) to polar coordinates (r, theta)
    System.out.println(java.lang.Math.cbrt(3)); //cbrt() method - returns the cube root
    System.out.println(java.lang.Math.ceil(3.14)); //ceil() method - returns the value of x rounded up to its nearest integer
    System.out.println(java.lang.Math.floor(3.14)); //floor() method - returns the value of x rounded down to its nearest integer
    System.out.println(java.lang.Math.copySign(4, -5)); //copySign() method - returns the first floating point x with the sign of the second floating point y
    System.out.println(java.lang.Math.cos(3.14)); //cos() method - returns the cosine of x (x is in radians)
    System.out.println(java.lang.Math.sin(1.5)); //sin() method - returns the sine of x (x is in radians)
    System.out.println(java.lang.Math.cos(0)); //cosh() method - returns the hyperbolic cosine of a double value
    System.out.println(java.lang.Math.sin(1)); //sinh() method() - returns the hyperbolic sine of a double value
    System.out.println(java.lang.Math.tan(-1)); //tan() method - returns the tangent of an angle
    System.out.println(java.lang.Math.tanh(0.5)); //tanh() method - returns the hyperbolic tangent of a double value
    System.out.println(java.lang.Math.exp(2.1232)); //exp() method - returns the value of Ex
    System.out.println(java.lang.Math.expm1(2.1232)); //expm1() method - returns ex-1
    System.out.println(java.lang.Math.getExponent(2.135f)); //getExponent() method - returns the unbiased exponent used in x
    System.out.println(java.lang.Math.sqrt(4)); //sqrt() method - returns the square root of x
    System.out.println(java.lang.Math.hypot(3, 4)); //hypot(x,y) method - returns sqrt(x2 +y2) without intermediate overflow or underflow
    System.out.println(java.lang.Math.IEEEremainder(6, 2)); //IEEEremainder() method - computes the remainder operation on x and y as prescribed by the IEEE 754 standard
    System.out.println(java.lang.Math.log(2.04)); //log() method - returns the natural logarithm (base E) of x
    System.out.println(java.lang.Math.log10(2.04)); //log10() method - returns the base 10 logarithm of x
    System.out.println(java.lang.Math.log1p(2.04)); //log1p() method - returns the natural logarithm (base E) of the sum of x and 1
    System.out.println(java.lang.Math.max(456,432)); //max() method - returns the number with the highest value
    System.out.println(java.lang.Math.min(101.12,101.33)); //min() method - returns the number with the lowest value
    System.out.println(java.lang.Math.nextAfter(34.01, 33)); //nextAfter() method - returns the floating point number adjacent to x in the direction of y
    System.out.println(java.lang.Math.nextUp(34.01)); //nextUp() method - returns the floating point value adjacent to x in the direction of positive infinity
    System.out.println(java.lang.Math.pow(2, 10)); //pow() method - returns the value of x to the power of y
    System.out.println(java.lang.Math.round(3.14)); //round() method - returns the value of x rounded to its nearest integer
    System.out.println(java.lang.Math.random()); //random() method - returns a random number between 0 and 1
    System.out.println(java.lang.Math.rint(3.14)); //rint() method - returns the double value that is closest to x and equal to a mathematical integer
    System.out.println(java.lang.Math.signum(-1.1)); //signum() method - returns the sign
    System.out.println(java.lang.Math.toDegrees(java.lang.Math.PI)); //toDegrees() method - converts an angle measured in radians to an approx. equivalent angle measured in degrees
    System.out.println(java.lang.Math.toRadians(180.0)); //toRadians() method - converts an angle measured in degrees to an approx. angle measured in radians
    System.out.println(java.lang.Math.ulp(-0.0f)); //random() method - returns the size of the unit of least precision (ulp) of x

    }
}
